mapper.select:
select count(*) from java.board_spring where
(writer like #{keyword} or subject like #{keyword} or content like #{keyword})
${searchkey} like #{keyword}

select num,writer,name,subject,content,subgroup,substep,sublevel,viewcount,regdate, (select count(*) from java.reply r where b.num = r.bnum) cnt from java.board_spring b, java.member m where b.writer = m.userid and
(writer like #{keyword} or subject like #{keyword} or content like #{keyword})
${searchkey} like #{keyword}
order by subgroup desc, substep asc limit #{start}, #{end}
